import React, { useState } from "react";

const InvestmentForm = (props) => {
  const [enteredCSavings, setEnteredCSavings] = useState("");
  const [enteredYSavings, setEnteredYSavings] = useState("");
  const [enteredInterest, setEnteredInterest] = useState("");
  const [enteredDuration, setEnteredDuration] = useState("");

  const inputChangeHandler = (input, value) => {
    if(input === 'cSavings'){
      setEnteredCSavings(value);
    }

  };

  const resetHandler = () => {
    setEnteredCSavings("");
    setEnteredYSavings("");
    setEnteredInterest("");
    setEnteredDuration("");
  };

  const formSubmitHandler = (event) => {
    event.preventDefault();

    const investmentData = {
      cSavings: +enteredCSavings,
      ySavings: +enteredYSavings,
      interest: +enteredInterest,
      duration: +enteredDuration,
    };

    props.onCalculateInvestmentData(investmentData);

    setEnteredCSavings(""); // submitted form init.
    setEnteredYSavings("");
    setEnteredInterest("");
    setEnteredDuration("");
  };

  return (
    <form className="form">
      <div className="input-group">
        <p>
          <label htmlFor="current-savings">Current Savings ($)</label>
          <input
            type="number"
            id="current-savings"
            value={enteredCSavings}
            onChange={(event) =>
              inputChangeHandler("cSavings", event.target.value)
            }
          />
        </p>
        <p>
          <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
          <input
            type="number"
            id="yearly-contribution"
            value={enteredYSavings}
            onChange={(event) =>
              inputChangeHandler("ySavings", event.target.value)
            }
          />
        </p>
      </div>
      <div className="input-group">
        <p>
          <label htmlFor="expected-return">
            Expected Interest (%, per year)
          </label>
          <input
            type="number"
            id="expected-return"
            value={enteredInterest}
            onChange={(event) =>
              inputChangeHandler("interest", event.target.value)
            }
          />
        </p>
        <p>
          <label htmlFor="duration">Investment Duration (years)</label>
          <input
            type="number"
            id="duration"
            value={enteredDuration}
            onChange={(event) =>
              inputChangeHandler("duration", event.target.value)
            }
          />
        </p>
      </div>
      <p className="actions">
        <button type="reset" className="buttonAlt" onClick={resetHandler}>
          Reset
        </button>
        <button type="submit" className="button" onClick={formSubmitHandler}>
          Calculate
        </button>
      </p>
    </form>
  );
};

export default InvestmentForm;
